import React from 'react';
import { motion } from 'framer-motion';
import { ArrowUpRight, Sun, Leaf, Zap } from 'lucide-react';
import './Hero.css';

const Hero = () => {
  return (
    <section className="hero">
      {/* Animated Background */}
      <div className="hero-background">
        <div className="ocean-waves">
          <div className="wave wave-1"></div>
          <div className="wave wave-2"></div>
          <div className="wave wave-3"></div>
        </div>
        <div className="jet-ski-wake"></div>
        <div className="floating-particles">
          {[...Array(20)].map((_, i) => (
            <div key={i} className="particle" style={{
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 3}s`,
              animationDuration: `${3 + Math.random() * 4}s`
            }}></div>
          ))}
        </div>
      </div>

      {/* Content */}
      <div className="hero-content">
        <motion.div
          className="hero-text"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.2 }}
        >
          <h1 className="hero-title">
            Choose the power of{' '}
            <span className="highlight">good</span>
          </h1>
          
          <motion.div
            className="hero-cta"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <a href="#video-section" className="hero-button">
              Your Way to Net-zero
              <ArrowUpRight size={20} />
            </a>
          </motion.div>
        </motion.div>

        {/* Floating Elements */}
        <motion.div
          className="floating-elements"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1.5, delay: 1 }}
        >
          <div className="floating-card card-1">
            <Sun className="card-icon" size={24} />
            <div className="card-text">Solar Power</div>
          </div>
          <div className="floating-card card-2">
            <Leaf className="card-icon" size={24} />
            <div className="card-text">Sustainability</div>
          </div>
          <div className="floating-card card-3">
            <Zap className="card-icon" size={24} />
            <div className="card-text">Clean Energy</div>
          </div>
        </motion.div>
      </div>

      {/* Scroll Indicator */}
      <motion.div
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 1.5 }}
      >
        <div className="scroll-text">Scroll to explore</div>
        <div className="scroll-arrow"></div>
      </motion.div>
    </section>
  );
};

export default Hero;
