import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Mail, Phone, MapPin, Send, ArrowRight, Sun, Leaf, Zap } from 'lucide-react';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    company: '',
    message: ''
  });

  const [animationPhase, setAnimationPhase] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setAnimationPhase((prev) => (prev + 1) % 4);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log('Form submitted:', formData);
  };

  const contactInfo = [
    {
      icon: <Mail size={24} />,
      title: "Email",
      value: "info@cleanco.com",
      link: "mailto:info@cleanco.com"
    },
    {
      icon: <Phone size={24} />,
      title: "Phone",
      value: "+91 22 62520000",
      link: "tel:+912262520000"
    },
    {
      icon: <MapPin size={24} />,
      title: "Address",
      value: "4th Floor, The International, 16 Maharshi Karve Road, New Marine Lines, Cross Road No.1, Churchgate, Mumbai 400 020",
      link: null
    }
  ];

  return (
    <section id="contact" className="contact">
      <div className="contact-container">
        <motion.div
          className="section-header"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">
            CONTACT US
          </h2>
          <h3 className="section-subtitle">
            Your Bridge to Smarter Energy
          </h3>
          <p className="section-description">
            Connect with us for tailored energy solutions designed for long-term impact.
          </p>
        </motion.div>

        <div className="contact-content">
          {/* 3D Animation Section */}
          <motion.div
            className="contact-animation"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <div className="animation-container">
              {/* Sun Animation */}
              <motion.div
                className="sun"
                animate={{
                  rotate: 360,
                  scale: [1, 1.1, 1],
                }}
                transition={{
                  rotate: { duration: 20, repeat: Infinity, ease: "linear" },
                  scale: { duration: 4, repeat: Infinity, ease: "easeInOut" }
                }}
              >
                <Sun size={80} />
                <div className="sun-rays"></div>
              </motion.div>

              {/* Solar Panel Array */}
              <motion.div
                className="solar-panels"
                animate={{
                  y: [0, -10, 0],
                }}
                transition={{
                  duration: 6,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                <div className="panel-row">
                  <div className="solar-panel panel-1"></div>
                  <div className="solar-panel panel-2"></div>
                  <div className="solar-panel panel-3"></div>
                </div>
                <div className="panel-row">
                  <div className="solar-panel panel-4"></div>
                  <div className="solar-panel panel-5"></div>
                  <div className="solar-panel panel-6"></div>
                </div>
              </motion.div>

              {/* Trees */}
              <div className="trees-container">
                <motion.div
                  className="tree tree-1"
                  animate={{
                    rotate: [0, 5, 0, -5, 0],
                  }}
                  transition={{
                    duration: 4,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  <div className="tree-trunk"></div>
                  <div className="tree-leaves"></div>
                </motion.div>

                <motion.div
                  className="tree tree-2"
                  animate={{
                    rotate: [0, -3, 0, 3, 0],
                  }}
                  transition={{
                    duration: 3.5,
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 0.5
                  }}
                >
                  <div className="tree-trunk"></div>
                  <div className="tree-leaves"></div>
                </motion.div>

                <motion.div
                  className="tree tree-3"
                  animate={{
                    rotate: [0, 4, 0, -4, 0],
                  }}
                  transition={{
                    duration: 4.5,
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 1
                  }}
                >
                  <div className="tree-trunk"></div>
                  <div className="tree-leaves"></div>
                </motion.div>
              </div>

              {/* Energy Particles */}
              <div className="energy-particles">
                {[...Array(8)].map((_, i) => (
                  <motion.div
                    key={i}
                    className="particle"
                    animate={{
                      y: [0, -100, 0],
                      x: [0, Math.random() * 40 - 20, 0],
                      opacity: [0, 1, 0],
                    }}
                    transition={{
                      duration: 3,
                      repeat: Infinity,
                      delay: i * 0.4,
                      ease: "easeInOut"
                    }}
                  >
                    <Zap size={12} />
                  </motion.div>
                ))}
              </div>

              {/* Floating Elements */}
              <motion.div
                className="floating-elements"
                animate={{
                  y: [0, -20, 0],
                }}
                transition={{
                  duration: 6,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                <div className="floating-leaf leaf-1">üçÉ</div>
                <div className="floating-leaf leaf-2">üçÉ</div>
                <div className="floating-leaf leaf-3">üçÉ</div>
              </motion.div>

              {/* Ground */}
              <div className="ground"></div>
            </div>
          </motion.div>

          {/* Contact Form Section */}
          <motion.div
            className="contact-form-section"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <div className="contact-info">
              <h3>Get in Touch</h3>
              <p>Ready to start your journey towards sustainable energy? Let's discuss how we can help you achieve your net-zero goals.</p>
              
              <div className="contact-details">
                {contactInfo.map((info, index) => (
                  <div key={index} className="contact-item">
                    <div className="contact-icon">
                      {info.icon}
                    </div>
                    <div className="contact-text">
                      <h4>{info.title}</h4>
                      {info.link ? (
                        <a href={info.link}>{info.value}</a>
                      ) : (
                        <p>{info.value}</p>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="contact-form">
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <input
                    type="text"
                    name="name"
                    placeholder="Your Name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <input
                    type="email"
                    name="email"
                    placeholder="Your Email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                  />
                </div>
                
                <div className="form-group">
                  <input
                    type="text"
                    name="company"
                    placeholder="Company Name"
                    value={formData.company}
                    onChange={handleChange}
                  />
                </div>
                
                <div className="form-group">
                  <textarea
                    name="message"
                    placeholder="Tell us about your energy needs..."
                    value={formData.message}
                    onChange={handleChange}
                    rows="5"
                    required
                  ></textarea>
                </div>
                
                <button type="submit" className="submit-btn">
                  Get Started Today
                  <Send size={20} />
                </button>
              </form>
            </div>
          </motion.div>
        </div>

        <motion.div
          className="contact-cta"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          viewport={{ once: true }}
        >
          <h3>Ready to Go Solar?</h3>
          <p>Join thousands of customers who have already made the switch to clean, renewable energy.</p>
          <div className="cta-buttons">
            <a href="#contact" className="cta-button primary">Get Started Today</a>
            <a href="#solutions" className="cta-button secondary">Learn More</a>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;
