import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import { Play, Pause, ArrowRight } from 'lucide-react';
import './Hero.css';

const Hero = () => {
  const [isPlaying, setIsPlaying] = useState(true);
  const [currentVideo, setCurrentVideo] = useState(0);
  const videoRefs = useRef([]);

  const heroVideos = [
    {
      videoUrl: "https://player.vimeo.com/external/434045526.sd.mp4?s=c27eecc69a27dbc4ff2b87d38afc35f1a9e7c02d&profile_id=164&oauth2_token_id=57447761",
      quote: "The sun is the only safe nuclear reactor, situated as it is some ninety-three million miles away.",
      author: "Stephanie Mills",
      title: "Bridging Your Business",
      subtitle: "to a Net-Zero Future",
      description: "Your Way to Net-zero",
      cta: "Scalable Solutions"
    },
    {
      videoUrl: "https://player.vimeo.com/external/328274.sd.mp4?s=8c4c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c&profile_id=164&oauth2_token_id=57447761",
      quote: "Solar energy is the most democratic form of energy. It's available to everyone, everywhere.",
      author: "Elon Musk",
      title: "Clean Energy,",
      subtitle: "Custom-built for your vision",
      description: "Navigating the road to net-zero can be complex. We make it simple. Our tailor-made solutions have helped prominent enterprises accelerate their net-zero goals - reliably, and cost-effectively.",
      cta: "Explore Solutions"
    },
    {
      videoUrl: "https://player.vimeo.com/external/434045526.sd.mp4?s=c27eecc69a27dbc4ff2b87d38afc35f1a9e7c02d&profile_id=164&oauth2_token_id=57447761",
      quote: "We are like tenant farmers chopping down the fence around our house for fuel when we should be using Nature's inexhaustible sources of energy.",
      author: "Nikola Tesla",
      title: "Sustainable Solutions",
      subtitle: "for Tomorrow's Challenges",
      description: "Creating innovative renewable energy solutions that power the future while preserving our planet for generations to come.",
      cta: "Discover Innovation"
    },
    {
      videoUrl: "https://player.vimeo.com/external/328274.sd.mp4?s=8c4c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c0c&profile_id=164&oauth2_token_id=57447761",
      quote: "The best time to plant a tree was 20 years ago. The second best time is now.",
      author: "Chinese Proverb",
      title: "Powering Progress",
      subtitle: "with Renewable Energy",
      description: "Join the global movement towards sustainable energy. Every step towards renewable power is a step towards a brighter future.",
      cta: "Join the Movement"
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentVideo((prev) => (prev + 1) % heroVideos.length);
    }, 8000); // Change video every 8 seconds
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Safely manage video playback
    const manageVideos = async () => {
      for (let i = 0; i < videoRefs.current.length; i++) {
        const videoRef = videoRefs.current[i];
        if (videoRef && videoRef.current) {
          try {
            if (i === currentVideo) {
              if (isPlaying) {
                // Only play if not already playing
                if (videoRef.current.paused) {
                  await videoRef.current.play();
                }
              } else {
                videoRef.current.pause();
              }
            } else {
              // Pause all other videos
              videoRef.current.pause();
            }
          } catch (error) {
            console.log('Video operation failed:', error);
            // Continue with other videos even if one fails
          }
        }
      }
    };

    manageVideos();
  }, [currentVideo, isPlaying]);

  const toggleVideo = () => {
    setIsPlaying(!isPlaying);
  };

  const handleVideoClick = (index) => {
    setCurrentVideo(index);
  };

  const handleVideoLoad = (index) => {
    // Ensure only the current video is set to autoplay
    if (index === 0) {
      const videoRef = videoRefs.current[index];
      if (videoRef && videoRef.current) {
        videoRef.current.play().catch(error => {
          console.log('Autoplay failed:', error);
        });
      }
    }
  };

  return (
    <section className="hero">
      {/* Multiple Video Backgrounds */}
      <div className="hero-videos-container">
        {heroVideos.map((video, index) => (
          <div
            key={index}
            className={`hero-video-wrapper ${index === currentVideo ? 'active' : ''}`}
          >
            <video
              ref={el => videoRefs.current[index] = { current: el }}
              className="hero-video"
              autoPlay={index === 0}
              muted
              loop
              playsInline
              onLoadedData={() => handleVideoLoad(index)}
            >
              <source src={video.videoUrl} type="video/mp4" />
            </video>
            <div className="video-overlay"></div>
          </div>
        ))}
      </div>

      {/* Video Controls */}
      <div className="video-controls">
        <button className="video-control-btn" onClick={toggleVideo}>
          {isPlaying ? <Pause size={20} /> : <Play size={20} />}
        </button>
      </div>

      {/* Hero Content */}
      <div className="hero-content">
        <motion.div
          className="hero-text"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          <motion.h1
            key={currentVideo}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.8 }}
            className="hero-title"
          >
            {heroVideos[currentVideo].title}
            <br />
            <span className="hero-subtitle">{heroVideos[currentVideo].subtitle}</span>
          </motion.h1>

          <motion.p
            key={`desc-${currentVideo}`}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            className="hero-description"
          >
            {heroVideos[currentVideo].description}
          </motion.p>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            className="hero-cta"
          >
            <a href="#solutions" className="cta-button">
              {heroVideos[currentVideo].cta}
              <ArrowRight size={20} />
            </a>
          </motion.div>
        </motion.div>

        {/* Quote Display */}
        <motion.div
          key={`quote-${currentVideo}`}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="hero-quote"
        >
          <blockquote>
            "{heroVideos[currentVideo].quote}"
          </blockquote>
          <cite>— {heroVideos[currentVideo].author}</cite>
        </motion.div>

        {/* Video Indicators */}
        <div className="video-indicators">
          {heroVideos.map((_, index) => (
            <button
              key={index}
              className={`video-indicator ${index === currentVideo ? 'active' : ''}`}
              onClick={() => handleVideoClick(index)}
            />
          ))}
        </div>
      </div>

      {/* Floating Elements */}
      <motion.div
        className="floating-elements"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1.5, delay: 1 }}
      >
        <div className="floating-card card-1">
          <div className="card-icon">☀️</div>
          <div className="card-text">Solar Power</div>
        </div>
        <div className="floating-card card-2">
          <div className="card-icon">🌱</div>
          <div className="card-text">Sustainability</div>
        </div>
        <div className="floating-card card-3">
          <div className="card-icon">⚡</div>
          <div className="card-text">Clean Energy</div>
        </div>
      </motion.div>
    </section>
  );
};

export default Hero;
