import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Menu, X, ChevronDown } from 'lucide-react';
import Logo from './Logo';
import './Header.css';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleDropdown = (dropdown) => {
    setActiveDropdown(activeDropdown === dropdown ? null : dropdown);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
    setActiveDropdown(null);
  };

  const menuItems = [
    { name: 'Home', href: '#home' },
    { name: 'Know Us', href: '#about' },
    { 
      name: 'Business', 
      href: '#solutions',
      dropdown: [
        { name: 'Overview', href: '#overview' },
        { name: 'Business Model', href: '#business-model' },
        { name: 'Industries Served', href: '#industries' }
      ]
    },
    { 
      name: 'Investors', 
      href: '#investors',
      dropdown: [
        { name: 'Overview', href: '#investor-overview' },
        { name: 'Corporate Governance', href: '#governance' }
      ]
    },
    { name: 'Careers', href: '#careers' },
    { name: 'Contact Us', href: '#contact' }
  ];

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="header-container">
        {/* Logo */}
        <div className="logo-container">
          <Link to="/" onClick={closeMobileMenu}>
            <Logo />
            <div className="logo-tagline">POWERING SUSTAINABILITY</div>
          </Link>
        </div>

        {/* Desktop Navigation */}
        <nav className="nav-desktop">
          <ul className="nav-list">
            {menuItems.map((item, index) => (
              <li key={index} className="nav-item">
                {item.dropdown ? (
                  <div className="dropdown-container">
                    <button
                      className="nav-link dropdown-toggle"
                      onClick={() => toggleDropdown(item.name)}
                    >
                      {item.name}
                      <ChevronDown size={16} />
                    </button>
                    {activeDropdown === item.name && (
                      <ul className="dropdown-menu">
                        {item.dropdown.map((dropdownItem, dropdownIndex) => (
                          <li key={dropdownIndex}>
                            <a href={dropdownItem.href} className="dropdown-link">
                              {dropdownItem.name}
                            </a>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                ) : (
                  <a href={item.href} className="nav-link">
                    {item.name}
                  </a>
                )}
              </li>
            ))}
          </ul>
        </nav>

        {/* Mobile Menu Button */}
        <button className="mobile-menu-btn" onClick={toggleMobileMenu}>
          {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>

        {/* Mobile Navigation */}
        <nav className={`nav-mobile ${isMobileMenuOpen ? 'open' : ''}`}>
          <ul className="mobile-nav-list">
            {menuItems.map((item, index) => (
              <li key={index} className="mobile-nav-item">
                {item.dropdown ? (
                  <div className="mobile-dropdown-container">
                    <button
                      className="mobile-nav-link dropdown-toggle"
                      onClick={() => toggleDropdown(item.name)}
                    >
                      {item.name}
                      <ChevronDown size={16} />
                    </button>
                    {activeDropdown === item.name && (
                      <ul className="mobile-dropdown-menu">
                        {item.dropdown.map((dropdownItem, dropdownIndex) => (
                          <li key={dropdownIndex}>
                            <a 
                              href={dropdownItem.href} 
                              className="mobile-dropdown-link"
                              onClick={closeMobileMenu}
                            >
                              {dropdownItem.name}
                            </a>
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                ) : (
                  <a 
                    href={item.href} 
                    className="mobile-nav-link"
                    onClick={closeMobileMenu}
                  >
                    {item.name}
                  </a>
                )}
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
