import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Menu, X } from 'lucide-react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const menuItems = [
    { name: 'Solar', href: '#solar' },
    { name: 'BESS', href: '#bess' },
    { name: 'CBG', href: '#cbg' },
    { name: 'About Us', href: '#about' }
  ];

  return (
    <header className={`header ${scrolled || location.pathname === '/solar' ? 'scrolled' : ''}`}>
      <div className="header-container">
        <div className="logo">
          <Link to="/">
            <img src="/images/sologo-aitools-bgremover.png" alt="CLEANCO Logo" />
          </Link>
        </div>
        <nav className="nav-links">
          <ul>
            <li><Link to="/solar" className={location.pathname === '/solar' ? 'active' : ''}>Solar</Link></li>
            <li><a href="#bess">BESS</a></li>
            <li><a href="#cbg">CBG</a></li>
            <li><a href="#about">About Us</a></li>
          </ul>
        </nav>
        <button className="get-started-btn">Get Started</button>
      </div>
    </header>
  );
};

export default Header;
