import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Linkedin, Youtube } from 'lucide-react';
import './Footer.css';
import './FooterBlack.css';

const Footer = () => {
  const currentYear = new Date().getFullYear();
  const [email, setEmail] = useState('');

  const handleSubscribe = (e) => {
    e.preventDefault();
    // Handle newsletter subscription
    console.log('Subscribing email:', email);
    setEmail('');
    // You can add actual subscription logic here
  };

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1,
        delayChildren: 0.1
      }
    }
  };

  const columnVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: [0.4, 0, 0.2, 1]
      }
    }
  };

  const bottomVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: [0.4, 0, 0.2, 1]
      }
    }
  };

  return (
    <footer 
      className="footer footer-black" 
      style={{ 
        backgroundColor: '#000000', 
        background: '#000000',
        backgroundImage: 'none',
        backgroundSize: 'auto',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: 'initial',
        color: '#ffffff',
        padding: '4rem 0 2rem',
        border: '2px solid red'
      }}
    >
      <div className="container">
        {/* Top Section */}
        <motion.div 
          className="footer-top"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.3 }}
        >
          <motion.div className="footer-column" variants={columnVariants}>
            <h4>Our Solutions</h4>
            <ul className="footer-links">
              <li><a href="#solar">Solar</a></li>
              <li><a href="#bess">BESS</a></li>
              <li><a href="#cbg">CBG</a></li>
            </ul>
          </motion.div>

          <motion.div className="footer-column" variants={columnVariants}>
            <h4>Company</h4>
            <ul className="footer-links">
              <li><a href="#about">About Us</a></li>
            </ul>
          </motion.div>

          <motion.div className="footer-column" variants={columnVariants}>
            <h4>Resources</h4>
            <ul className="footer-links">
              <li><a href="#menu1">Menu 1</a></li>
            </ul>
          </motion.div>

          <motion.div className="footer-column" variants={columnVariants}>
            <h4>Stay Updated</h4>
            <p className="newsletter-text">
              Subscribe to our newsletter for the latest news and insights on renewable energy solutions.
            </p>
            <form className="newsletter-form" onSubmit={handleSubscribe}>
              <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <button type="submit">Subscribe</button>
            </form>
          </motion.div>
        </motion.div>

        {/* Middle Section - Divider */}
        <div className="footer-divider"></div>

        {/* Bottom Section */}
        <motion.div 
          className="footer-bottom"
          variants={bottomVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.3 }}
        >
          <div className="footer-bottom-content">
            <p className="copyright">
              © {currentYear} Cleanco Group of Companies. All rights reserved.
            </p>
            
            <div className="footer-links-center">
              <a href="#terms">Terms and Conditions</a>
              <a href="#privacy">Privacy Policy</a>
            </div>
            
            <div className="social-links">
              <a href="#linkedin" className="social-link" aria-label="LinkedIn">
                <Linkedin size={20} />
              </a>
              <a href="#youtube" className="social-link" aria-label="YouTube">
                <Youtube size={20} />
              </a>
            </div>
          </div>
        </motion.div>
      </div>
    </footer>
  );
};

export default Footer;
