import React from 'react';
import { motion } from 'framer-motion';
import { Home, Building, Zap, Settings, Shield, TrendingUp } from 'lucide-react';
import './Services.css';

const Services = () => {
  const services = [
    {
      icon: <Home size={32} />,
      title: "Residential Solar",
      description: "Complete solar solutions for homeowners with custom design and installation.",
      features: ["Custom System Design", "Professional Installation", "25-Year Warranty", "Energy Monitoring"]
    },
    {
      icon: <Building size={32} />,
      title: "Commercial Solar",
      description: "Large-scale solar installations for businesses and commercial properties.",
      features: ["Scalable Solutions", "ROI Optimization", "Maintenance Plans", "Energy Analytics"]
    },
    {
      icon: <Zap size={32} />,
      title: "Energy Storage",
      description: "Battery storage solutions to maximize your solar energy independence.",
      features: ["Battery Backup", "Grid Independence", "Peak Shaving", "Emergency Power"]
    },
    {
      icon: <Settings size={32} />,
      title: "System Maintenance",
      description: "Comprehensive maintenance and monitoring services for optimal performance.",
      features: ["Regular Inspections", "Performance Monitoring", "Cleaning Services", "Repair Support"]
    }
  ];

  return (
    <section className="services-section">
      <div className="services-container">
        <motion.div
          className="services-header"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">
            Our <span className="highlight">Services</span>
          </h2>
          <p className="section-subtitle">
            Comprehensive solar energy solutions designed to meet your specific needs and maximize your investment.
          </p>
        </motion.div>

        <div className="services-grid">
          {services.map((service, index) => (
            <motion.div
              key={index}
              className="service-card"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              <div className="service-icon">{service.icon}</div>
              <h3>{service.title}</h3>
              <p>{service.description}</p>
              <ul className="service-features">
                {service.features.map((feature, featureIndex) => (
                  <li key={featureIndex}>{feature}</li>
                ))}
              </ul>
            </motion.div>
          ))}
        </div>

        <motion.div
          className="services-cta"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <div className="cta-content">
            <h3>Ready to Start Your Solar Journey?</h3>
            <p>Get a free consultation and quote for your solar installation project.</p>
            <a href="/contact" className="cta-button">Get Free Quote</a>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Services;
