import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import './Header.css';

const Header = () => {
  	const [scrolled, setScrolled] = useState(false);
	const location = useLocation();
	const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      const offset = window.scrollY;
      if (offset > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToSection = (sectionId) => {
    // If we're not on the home page, navigate there first
    if (location.pathname !== '/') {
      navigate('/');
      // Wait for navigation to complete, then scroll
      setTimeout(() => {
        const element = document.getElementById(sectionId);
        if (element) {
          element.scrollIntoView({ 
            behavior: 'smooth',
            block: 'start'
          });
        }
      }, 100);
    } else {
      // If we're already on the home page, just scroll
      const element = document.getElementById(sectionId);
      if (element) {
        element.scrollIntoView({ 
          behavior: 'smooth',
          block: 'start'
        });
      }
    }
  };

  const handleNavClick = (e, sectionId) => {
    e.preventDefault();
    scrollToSection(sectionId);
  };

  return (
    <header className={`header ${scrolled || location.pathname === '/solar' || location.pathname === '/bess' || location.pathname === '/cbg' || location.pathname === '/about' ? 'scrolled' : ''}`}>
      <div className="header-container">
        <div className="logo">
          <Link to="/">
            <img src="/images/cleanco_logo_f.png" alt="CLEANCO Logo" />
          </Link>
        </div>
        <nav className="nav-links">
          <ul>
            <li><a href="#solar" onClick={(e) => handleNavClick(e, 'solar')} className={location.pathname === '/solar' ? 'active' : ''}>Solar</a></li>
            <li><a href="#bess" onClick={(e) => handleNavClick(e, 'bess')} className={location.pathname === '/bess' ? 'active' : ''}>BESS</a></li>
            <li><a href="#cbg" onClick={(e) => handleNavClick(e, 'cbg')} className={location.pathname === '/cbg' ? 'active' : ''}>CBG</a></li>
            <li><a href="#about" onClick={(e) => handleNavClick(e, 'about')} className={location.pathname === '/about' ? 'active' : ''}>About Us</a></li>
          </ul>
        </nav>
        <button className="get-started-btn">Get Started</button>
      </div>
    </header>
  );
};

export default Header;
