import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Menu, X, ChevronDown } from 'lucide-react';
import './Header.css';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isInvestorsDropdownOpen, setIsInvestorsDropdownOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const toggleInvestorsDropdown = () => {
    setIsInvestorsDropdownOpen(!isInvestorsDropdownOpen);
  };

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="header-container">
        {/* Logo */}
        <div className="logo">
          <Link to="/">
            <div className="logo-text">
              <span className="logo-clean">CLEAN</span>
              <span className="logo-co">CO</span>
            </div>
            <div className="logo-tagline">POWERING SUSTAINABILITY</div>
          </Link>
        </div>

        {/* Desktop Navigation */}
        <nav className="nav-desktop">
          <ul className="nav-list">
            <li><Link to="/" className="nav-link active">Home</Link></li>
            <li><Link to="/about" className="nav-link">Know Us</Link></li>
            <li><Link to="/business" className="nav-link">Business</Link></li>
            <li className="dropdown">
              <button 
                className="nav-link dropdown-toggle"
                onClick={toggleInvestorsDropdown}
              >
                Investors
                <ChevronDown size={16} />
              </button>
              {isInvestorsDropdownOpen && (
                <ul className="dropdown-menu">
                  <li><Link to="/investors/financials">Financial Reports</Link></li>
                  <li><Link to="/investors/stock">Stock Information</Link></li>
                  <li><Link to="/investors/events">Events & Presentations</Link></li>
                </ul>
              )}
            </li>
            <li><Link to="/careers" className="nav-link">Careers</Link></li>
          </ul>
        </nav>

        {/* CTA Button */}
        <div className="header-cta">
          <Link to="/contact" className="cta-button">
            Contact Us
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M7 17L17 7M17 7H7M17 7V17"/>
            </svg>
          </Link>
        </div>

        {/* Mobile Menu Button */}
        <button className="mobile-menu-button" onClick={toggleMobileMenu}>
          {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Navigation */}
      {isMobileMenuOpen && (
        <nav className="nav-mobile">
          <ul className="mobile-nav-list">
            <li><Link to="/" className="mobile-nav-link" onClick={toggleMobileMenu}>Home</Link></li>
            <li><Link to="/about" className="mobile-nav-link" onClick={toggleMobileMenu}>Know Us</Link></li>
            <li><Link to="/business" className="mobile-nav-link" onClick={toggleMobileMenu}>Business</Link></li>
            <li><Link to="/investors" className="mobile-nav-link" onClick={toggleMobileMenu}>Investors</Link></li>
            <li><Link to="/careers" className="mobile-nav-link" onClick={toggleMobileMenu}>Careers</Link></li>
            <li><Link to="/contact" className="mobile-nav-link" onClick={toggleMobileMenu}>Contact Us</Link></li>
          </ul>
        </nav>
      )}
    </header>
  );
};

export default Header;
